<?php		ob_implicit_flush();	set_time_limit(0);	ini_set('memory_limit',-1);		require_once('simple_html_dom.php');	require_once('sql.php');		//читаем в несколько попыток	function read($url)	{		$i = 0;		do		{			$content = @file_get_contents($url);			$i++;		}		while($content == '' && $i < 5);		if ($content == '' || !$content) return false;				return $content;	}?>