<?php	function get_microtime()	{			$mtime = microtime();		$mtime = explode(" ",$mtime);		$return ($mtime[1] + $mtime[0]);	}	    function convert_date_to_text($date)    {		if ($date == '0000-00-00') return "";				$date = explode('-', $date);		$month = round($date[1]);						$month_names = array(						  		'',								'января',								'февраля',								'марта',								'апреля',								'мая',								'июня',								'июля',								'августа',								'сентября',								'октября',								'ноября',								'декабря'								);				$month = $month_names[$month];		$day = date("j", mktime(0, 0, 0, $date[1], $date[2], $date[0])); //день без нулей		$date = $day . ' ' . $month . ' ' . $date[0];		return $date;    }?>